package com.xiyu.schedulix.controller;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;

import com.xiyu.schedulix.model.SchedulixJob;
import com.xiyu.schedulix.repository.SchedulixJobRepository;
@Controller
public class SchedulixJobController {
	
	@Autowired // This means to get the bean called userRepository
    // Which is auto-generated by Spring, we will use it to handle the data
    private SchedulixJobRepository sjr;

	public void addNewJOB (String WENJIAN_ID, String JOB_ID,String STATUS) {

		SchedulixJob sj = new SchedulixJob();
        sj.setWENJIAN_ID(WENJIAN_ID);
        sj.setJOB_ID(JOB_ID);
		sj.setSTATUS(STATUS);
		sjr.save(sj);
	//	return "Saved";
	}
}